
public class ArrayTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String weeks[] = new String[7];
		weeks[0] = "Mon";
		weeks[1] = "Tue";
		weeks[2] = "Wed";
		weeks[3] = "Thu";
		weeks[4] = "Fri";
		weeks[5] = "Sat";
		weeks[6] = "Sun";
		
		for (int i=0; i<weeks.length; i++)
		{
			System.out.println(weeks[i]);
		}
	}

}
